descriptor.templateCss:
.legend {
    font-size: 13px;
    line-height: 10px;
}

.legend table { 
    border-spacing: 0px;
    border-collapse: separate;
}

.mouse-events .flot-overlay {
    cursor: crosshair; 
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.flot = new IotopFlot(self.ctx);
    var widgetChart =  self.ctx.$containerParent["0"];
    var widgetHtml = widgetChart.offsetParent;
    var widgetFull = widgetHtml.offsetParent.offsetParent;
    widgetFull.style.padding = "0px";
    widgetFull.style.backgroundColor = "#eb4333";
    widgetFull.style.borderRadius = "15px";
    widgetHtml.style.backgroundColor = "#fff";
    widgetFull.firstElementChild.style.alignSelf = "center";
    widgetFull.firstElementChild.style.height = "130px";
    widgetFull.firstElementChild.style.width = "100%";
    widgetFull.firstElementChild.firstElementChild.style.height = "100%";
    widgetFull.firstElementChild.firstElementChild.style.maxHeight = "100%";
    widgetFull.firstElementChild.firstElementChild.style.paddingTop = "25px";
    widgetFull.firstElementChild.firstElementChild.style.width = "100%";
    widgetFull.firstElementChild.firstElementChild.style.textAlign = "center";
    widgetFull.firstElementChild.firstElementChild.firstElementChild.style.height = "100px";
    widgetFull.firstElementChild.firstElementChild.firstElementChild.style.textAlign = "-webkit-center";
    widgetFull.firstElementChild.firstElementChild.firstElementChild.firstElementChild.style.display = "table";
    widgetFull.firstElementChild.firstElementChild.firstElementChild.firstElementChild.style.paddingBottom = "10px";
    widgetFull.firstElementChild.firstElementChild.firstElementChild.firstElementChild.style.marginLeft = "-20px";
    var divTotalText = document.createElement("DIV");
    divTotalText.innerHTML = "teljes fogyasztás";
    var divTotal = document.createElement("P");
    divTotal.innerHTML = self.ctx.total + " kW";
    var att = document.createAttribute("id");
    att.value = "totalConsumptionNumber";
    divTotal.setAttributeNode(att);
    divTotal.style.margin = "0";
    divTotal.style.fontWeight = "700";
    divTotal.style.fontSize = "35px";
    divTotal.style.color = "#eb4333";
    divTotalText.style.backgroundColor = "#fff";
    divTotalText.style.textAlign = "center";
    divTotalText.style.paddingTop = "10px";
    divTotalText.style.fontSize = "12px";
    divTotalText.appendChild(divTotal);
    widgetFull.insertBefore(divTotalText, widgetFull.childNodes[1]);
};

self.onDataUpdated = function() {
    self.ctx.flot.update();
    var widgetChart =  self.ctx.$containerParent["0"];
    var widgetHtml = widgetChart.offsetParent;
    var widgetFull = widgetHtml.offsetParent.offsetParent;
    $('#totalConsumptionNumber', widgetFull)[0].innerHTML = self.ctx.total + " kW";
};

self.onResize = function() {
    self.ctx.flot.resize();
};

self.onEditModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
};

self.onMobileModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
};

self.getSettingsSchema = function() {
    return IotopFlot.settingsSchema('graph');
};

self.getDataKeySettingsSchema = function() {
    return IotopFlot.datakeySettingsSchema(true, 'graph');
};

self.onDestroy = function() {
    self.ctx.flot.destroy();
};

